mapper.select:
select * from 
(select row_number() over(order by ymno) rnum, a.* from ymember a 

where gender=#{gender}


where (gender='Male' or gender='Female')


and ${category} like '%'||#{searchWord}||'%' 


and ( ymno like '%'||#{searchWord}||'%' or name like '%'||#{searchWord}||'%')

)
where rnum between #{firstRow} and #{endRow}
